mapper.select:
SELECT NVL(cnt, 0) as dept_count FROM department d, (SELECT member_department, COUNT(member_department) as cnt FROM member GROUP BY member_department) m WHERE member_department(+) = department_id AND department_id in
#{dept.department_id}
ORDER BY department_id

SELECT NVL(cnt, 0) as dept_count FROM department d, (SELECT member_department, COUNT(member_department) as cnt FROM member GROUP BY member_department) m WHERE member_department(+) = department_id AND department_name LIKE #{department_name} AND department_id in
#{dept.department_id}
ORDER BY department_id
